INSERT INTO public.chain_of_stores (chain_name,description,chain_image) VALUES
	 ('All4Life','Ищите магазин, где вы можете найти все, что нужно для вашего дома и быта? У нас есть ответ! Мы предлагаем широкий ассортимент товаров, чтобы удовлетворить все ваши потребности. От еды до товаров для уборки и домашних аксессуаров - мы заботимся о вашем комфорте!','https://imgur.com/XzgcuSv'),
